        ...prevData,


      }
    });
  }

  return (
    <>
      <div className="main_note">
        <form>
          <input
          name =''
            type="text"
            value={note.title}
            onChange={InputEvent}
            placeholder="Title"
          />
          <textarea
            onChange={InputEvent}
            column=""
            rows=""
            value={note.content}
            placeholder="Write a note..."
            autoComplete="off"
          ></textarea>
          <Button>
            <AddIcon className="plus_sign" />
          </Button>
        </form>
      </div>
    </>
  );
};

export default CreateNote;
